    function getPetLevelUpExp(id, lv) {
        if (lv <= 1)
            return 100;
        if (lv === 2)
            return 200;
        if (lv === 3)
            return 400;
        return 400 + (lv - 3) * 200;
    }
    exports.getPetLevelUpExp = getPetLevelUpExp;
